import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DAYS = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const SUBJECTS = [
  "Calculus 1", "Calculus 2", "Vector Calculus", "Differential Equations", "Linear Algebra", "Probability", 
  "Physics 1", "Physics 2", "Physics 3", "General Chemistry", "Organic Chemistry", "Biochemistry", 
  "Electronics I", "Electronics II", "Digital Logic", "Computer Architecture", "Data Structures and Algorithms",
  "Operating Systems", "Computer Networks", "Software Engineering", 
  "Concrete Structures", "Steel Structures", "Transportation Engineering", "Environmental Engineering",
  "Thermodynamics", "Fluid Mechanics", "Heat Transfer", "Mechanical Design", "Manufacturing Processes"
];

function UpdatePreference({ userId }) {
  const navigate = useNavigate();
  const [selectedDays, setSelectedDays] = useState([]);
  const [subjectStates, setSubjectStates] = useState(() =>
    SUBJECTS.reduce((acc, subject) => ({ ...acc, [subject]: "none" }), {})
  );
  const [message, setMessage] = useState('');

  const handleDayClick = (day) => {
    setSelectedDays(prev =>
      prev.includes(day) ? prev.filter(d => d !== day) : [...prev, day]
    );
  };

  const toggleSubject = (subject) => {
    setSubjectStates(prev => {
      const nextState = prev[subject] === "none"
        ? "learn"
        : prev[subject] === "learn"
        ? "teach"
        : "none";
      return { ...prev, [subject]: nextState };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const subjectsToLearn = [];
    const subjectsToTeach = [];

    Object.entries(subjectStates).forEach(([subject, state]) => {
      if (state === "learn") subjectsToLearn.push(subject);
      if (state === "teach") subjectsToTeach.push(subject);
    });

    const preferenceData = {
      availableDays: selectedDays.join(', '),
      subjectsToLearn: subjectsToLearn.join(', '),
      subjectsToTeach: subjectsToTeach.join(', ')
    };

    axios.post(`/api/user/${userId}/preference`, preferenceData)
      .then(() => {
        setMessage('Preference updated successfully.');
        navigate('/matchlist');
      })
      .catch(error => {
        setMessage(error.response?.data || 'Update failed.');
      });
  };

  // Styles
  const styles = {
container: {
  width: '75vw',               // 75% of viewport width
  margin: '2rem auto',         // vertical spacing and center horizontally
  padding: '2rem',
  boxSizing: 'border-box'
},

    heading: {
      textAlign: 'center',
      marginBottom: '1rem'
    },
    fieldset: {
      marginBottom: '2rem',
      border: '1px solid #ccc',
      padding: '1rem',
      borderRadius: '6px'
    },
    dayPillContainer: {
      display: 'flex',
      flexWrap: 'wrap',
      gap: '10px',
      marginTop: '10px'
    },
    dayPill: (selected) => ({
      padding: '8px 14px',
      border: '1px solid #ccc',
      borderRadius: '20px',
      backgroundColor: selected ? '#5ccdc1' : '#f5f5f5',
      color: selected ? 'white' : '#333',
      fontWeight: selected ? 'bold' : 'normal',
      cursor: 'pointer',
      transition: 'all 0.3s ease'
    }),
    subjectToggleRow: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      margin: '6px 0',
      padding: '0.5rem 0.5rem',
      borderBottom: '1px solid #eee'
    },
    subjectName: {
      flex: '1',
      fontSize: '0.95rem'
    },
    toggleButton: (state) => ({
      padding: '5px 12px',
      marginLeft: '1rem',
      borderRadius: '20px',
      border: '1px solid #ccc',
      backgroundColor:
        state === "learn"
          ? '#fbbf24' // yellow
          : state === "teach"
          ? '#34d399' // green
          : '#e5e7eb', // gray
      color: state === "none" ? '#333' : 'white',
      cursor: 'pointer',
      fontSize: '0.8rem',
      minWidth: '80px',
      textAlign: 'center'
    }),
    buttonContainer: {
      textAlign: 'center',
      marginTop: '1.5rem'
    },
    error: {
      color: 'red',
      textAlign: 'center',
      marginTop: '1rem'
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Update Preferences</h2>
      <form onSubmit={handleSubmit}>
        <fieldset style={styles.fieldset}>
          <legend><b>Available Days</b></legend>
          <div style={styles.dayPillContainer}>
            {DAYS.map(day => (
              <span
                key={day}
                style={styles.dayPill(selectedDays.includes(day))}
                onClick={() => handleDayClick(day)}
              >
                {day}
              </span>
            ))}
          </div>
        </fieldset>

        <fieldset style={styles.fieldset}>
          <legend><b>Subject Preferences</b></legend>
          {SUBJECTS.map(subject => (
            <div key={subject} style={styles.subjectToggleRow}>
              <span style={styles.subjectName}>{subject}</span>
              <button
                type="button"
                style={styles.toggleButton(subjectStates[subject])}
                onClick={() => toggleSubject(subject)}
              >
                {subjectStates[subject] === "none"
                  ? "None"
                  : subjectStates[subject] === "learn"
                  ? "Learn üìö"
                  : "Teach üßë‚Äçüè´"}
              </button>
            </div>
          ))}
        </fieldset>

        <div style={styles.buttonContainer}>
          <button type="submit">Update Preference</button>
        </div>
      </form>
      {message && <p style={styles.error}>{message}</p>}
    </div>
  );
}

export default UpdatePreference;
