import React, { useState, useEffect } from 'react';
import api from '../api';
import TinderCard from 'react-tinder-card';

function Dashboard() {
  const [matches, setMatches] = useState([]);
  const [message, setMessage] = useState('');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    if (userId) {
      fetchMatches();
    } else {
      setMessage("Please login to view matches.");
    }
  }, [userId]);

  const fetchMatches = async () => {
    try {
      const response = await api.get(`/api/matches/find/${userId}`);
      setMatches(response.data);
    } catch (error) {
      console.error(error);
      setMessage("Error fetching matches.");
    }
  };

  const swiped = async (direction, match) => {
    console.log("Swiped " + direction, match);
    if (direction === 'right') {
      try {
        // Call backend swipe endpoint with query params user1Id and user2Id
        await api.post('/api/matches/swipe', null, {
          params: { user1Id: userId, user2Id: match.user.id }
        });
        alert("Swipe recorded!");
      } catch (error) {
        console.error(error);
        alert("Error while swiping.");
      }
    }
  };

  return (
    <div>
      <h2>Dashboard</h2>
      { message && <p>{message}</p> }
      <div className="cardContainer">
        {matches.length > 0 ? (
          matches.map((match, index) => (
            <TinderCard
              key={index}
              onSwipe={(dir) => swiped(dir, match)}
              preventSwipe={['up', 'down']}
            >
              <div className="card">
                <h3>{match.user.name}</h3>
                <p>Common Days: {match.commonDays.join(', ')}</p>
                <p>Common Subjects: {match.commonSubjects.join(', ')}</p>
                <p>Match Score: {match.matchScore}</p>
              </div>
            </TinderCard>
          ))
        ) : (
          <p>No matches available.</p>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
