// src/components/Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../firebase';
import { createUserWithEmailAndPassword, updateProfile } from 'firebase/auth';
import axios from 'axios';

function Signup({ setUserId }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    major: '',
    year: ''
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
      e.preventDefault();
      createUserWithEmailAndPassword(auth, formData.email, formData.password)
        .then((userCredential) => {
          const user = userCredential.user;
          // Update display name
          updateProfile(user, { displayName: formData.name })
            .then(() => {
              user.getIdToken().then((idToken) => {
                axios.post('http://localhost:8080/api/auth/signup', { ...formData, firebaseToken: idToken })
                  .then(response => {
                    setMessage(response.data);
                    navigate('/login');
                  })
                  .catch(error => {
                    setMessage(error.response?.data || 'Signup failed');
                  });
              });
            });
        })
        .catch((error) => {
          setMessage(error.message || 'Signup failed');
        });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="name" 
          placeholder="Name" 
          onChange={handleChange} 
          value={formData.name} 
          required 
        /><br/><br/>
        <input 
          type="email" 
          name="email" 
          placeholder="Email (@cooper.edu)" 
          onChange={handleChange} 
          value={formData.email} 
          required 
        /><br/><br/>
        <input 
          type="password" 
          name="password" 
          placeholder="Password (min 9 characters)" 
          onChange={handleChange} 
          value={formData.password} 
          required 
        /><br/><br/>
        <select name="major" value={formData.major} onChange={handleChange} required>
          <option value="">Select Major</option>
          <option value="Electrical Engineering">Electrical Engineering</option>
          <option value="Mechanical Engineering">Mechanical Engineering</option>
          <option value="Chemical Engineering">Chemical Engineering</option>
          <option value="Civil Engineering">Civil Engineering</option>
          <option value="General Engineering">General Engineering</option>
          <option value="Computer Science">Computer Science</option>
        </select><br/><br/>
        <input 
          type="number" 
          name="year" 
          placeholder="Year (2020-2050)" 
          onChange={handleChange} 
          value={formData.year} 
          min="2020" 
          max="2050" 
          required 
        /><br/><br/>
        <button type="submit">Sign Up</button>
      </form>
      {message && <p style={{ color: 'red' }}>{message}</p>}
    </div>
  );
}

export default Signup;
