import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function MatchList({ userId }) {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');
  const [bottomMessage, setBottomMessage] = useState('');
  const navigate = useNavigate();

  const cuteAnimalEmojiMap = {
    "electrical engineering": "🐭",
    "mechanical engineering": "🐱",
    "civil engineering": "🐶",
    "chemical engineering": "🐹",
    "general engineering": "🐻",
    "computer science": "🦊"
  };

  const getEngineerEmoji = (user) => {
    if (user.major) {
      const normalizedMajor = user.major.trim().toLowerCase();
      return cuteAnimalEmojiMap[normalizedMajor] || "🐱";
    }
    return "🐱";
  };

  const fetchMatches = () => {
    axios.get(`/api/matches/find/${userId}`)
      .then(response => {
        if (Array.isArray(response.data)) {
          setMatches(response.data);
          setError('');
        } else {
          setMatches([]);
          setError(response.data);
        }
      })
      .catch(err => {
        setError(err.response?.data || 'Error fetching matches');
        setMatches([]);
      });
  };

  useEffect(() => {
    if (userId) fetchMatches();
  }, [userId]);

  const getUsername = (user) => {
    return user.name && user.name.trim() !== "" ? user.name : user.email.split('@')[0];
  };

  const onSwipe = (direction, matchUserId) => {
    if (direction === 'right') {
      axios.post(`/api/matches/swipe?user1Id=${userId}&user2Id=${matchUserId}`)
        .then(response => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(response.data?.message || '');
        })
        .catch(err => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(err.response?.data || 'Error processing swipe.');
        });
    } else {
      setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
      setBottomMessage('');
    }
  };

  const styles = {
    page: {
      background: 'linear-gradient(135deg, #e0f7fa, #ffffff)',
      minHeight: '100vh',
      padding: '2rem 0',
      fontFamily: 'Arial, sans-serif',
    },
    header: {
      textAlign: 'center',
      color: '#2c6e6a',
      marginBottom: '1rem',
    },
    cardContainer: {
      position: 'relative',
      width: '340px',
      height: '500px',
      margin: 'auto',
    },
    card: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      borderRadius: '20px',
      boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
      backgroundColor: '#ffffff',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
    },
    emoji: {
      fontSize: '5rem',
      marginTop: '2rem',
    },
    cardInfo: {
      marginTop: 'auto',
      background: 'linear-gradient(180deg, rgba(92,205,193,0.9), #2c6e6a)',
      color: '#fff',
      padding: '1rem',
      textAlign: 'center',
    },
    username: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '0.5rem',
    },
    buttonContainer: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '1rem',
    },
    swipeButton: {
      borderRadius: '50%',
      width: '60px',
      height: '60px',
      border: 'none',
      color: '#fff',
      fontSize: '1.5rem',
      cursor: 'pointer',
      boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
    },
    acceptButton: {
      backgroundColor: '#4CAF50',
    },
    rejectButton: {
      backgroundColor: '#F44336',
    },
    bottomMessage: {
      textAlign: 'center',
      color: '#2c6e6a',
      marginTop: '1rem',
    },
    updateBtn: {
      padding: '0.6rem 1.5rem',
      borderRadius: '20px',
      border: 'none',
      backgroundColor: '#5ccdc1',
      color: '#fff',
      cursor: 'pointer',
      marginBottom: '1.5rem',
    }
  };

  return (
    <div style={styles.page}>
      <h2 style={styles.header}>Find Your StudBuds!</h2>
      <div style={{ textAlign: 'center' }}>
        <button style={styles.updateBtn} onClick={() => navigate('/update')}>Update Preferences</button>
      </div>

      {error && <p style={styles.bottomMessage}>{error}</p>}

      <div style={styles.cardContainer}>
        {matches.length > 0 ? matches.map(match => (
          <TinderCard
            key={match.user.id}
            onSwipe={(dir) => onSwipe(dir, match.user.id)}
            preventSwipe={[]}
          >
            <div style={styles.card}>
              <div style={{ textAlign: 'center' }}>
                <div style={styles.emoji}>{getEngineerEmoji(match.user)}</div>
              </div>
              <div style={styles.cardInfo}>
                <div style={styles.username}>{getUsername(match.user)}</div>
                <p><strong>Common Days:</strong> {match.commonDays.join(', ')}</p>
                <p><strong>Common Subjects:</strong> {match.commonSubjects.join(', ')}</p>
                <div style={styles.buttonContainer}>
                  <button className="pressable" style={{...styles.swipeButton, ...styles.rejectButton}} onClick={() => onSwipe('left', match.user.id)}>✖️</button>
                  <button className="pressable" style={{...styles.swipeButton, ...styles.acceptButton}} onClick={() => onSwipe('right', match.user.id)}>✔️</button>
                </div>
              </div>
            </div>
          </TinderCard>
        )) : (
          <p style={styles.bottomMessage}>No matches available.</p>
        )}
      </div>

      {bottomMessage && <p style={styles.bottomMessage}>{bottomMessage}</p>}
    </div>
  );
}

export default MatchList;
