import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function MatchList({ userId }) {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');
  const [bottomMessage, setBottomMessage] = useState('');
  const navigate = useNavigate();

  const cuteAnimalEmojiMap = {
    "electrical engineering": "🐭",
    "mechanical engineering": "🐱",
    "civil engineering": "🐶",
    "chemical engineering": "🐹",
    "general engineering": "🐻",
    "computer science": "🦊"
  };

  const getEngineerEmoji = (user) => {
    const normalizedMajor = user.major?.trim().toLowerCase();
    return cuteAnimalEmojiMap[normalizedMajor] || "🐱";
  };

  const fetchMatches = () => {
    axios.get(`/api/matches/find/${userId}`)
      .then(response => {
        if (Array.isArray(response.data)) {
          setMatches(response.data);
          setError('');
        } else {
          setMatches([]);
          setError(response.data);
        }
      })
      .catch(err => {
        setError(err.response?.data || 'Error fetching matches');
        setMatches([]);
      });
  };

  useEffect(() => {
    if (userId) fetchMatches();
  }, [userId]);

  const getUsername = (user) => {
    return user.name && user.name.trim() !== "" ? user.name : user.email.split('@')[0];
  };

  const onSwipe = (direction, matchUserId) => {
    if (direction === 'right') {
      axios.post(`/api/matches/swipe?user1Id=${userId}&user2Id=${matchUserId}`)
        .then(response => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(response.data?.message || '');
        })
        .catch(err => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(err.response?.data || 'Error processing swipe.');
        });
    } else if (direction === 'left') {
      setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
      setBottomMessage('');
    }
  };

  const onCardLeftScreen = (matchUserId) => {
    console.log(`Card for user ${matchUserId} left the screen`);
  };

  const styles = {
    page: {
      background: 'linear-gradient(135deg, #e0f7fa, #ffffff)',
      minHeight: '100vh',
      padding: '2rem 0',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      color: '#5ccdc1',
      fontSize: '2rem',
      marginBottom: '1rem'
    },
    button: {
      padding: '0.75rem 1.5rem',
      fontSize: '1rem',
      backgroundColor: '#5ccdc1',
      color: '#fff',
      border: 'none',
      borderRadius: '20px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease'
    },
    cardContainer: {
      position: 'relative',
      width: '340px',
      height: '520px',
      margin: '0 auto'
    },
    card: {
      position: 'absolute',
      backgroundColor: '#ffffff',
      width: '100%',
      height: '100%',
      boxShadow: '0 6px 12px rgba(0,0,0,0.15)',
      borderRadius: '20px',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column'
    },
    emoji: {
      fontSize: '7rem',
      margin: 'auto'
    },
    cardInfo: {
      backgroundColor: '#2c6e6a',
      padding: '1rem',
      color: '#ffffff',
      borderBottomLeftRadius: '20px',
      borderBottomRightRadius: '20px'
    },
    username: {
      fontSize: '1.6rem',
      margin: '0.5rem 0',
      textAlign: 'center'
    },
    details: {
      fontSize: '0.9rem',
      margin: '0.2rem 0'
    },
    buttonGroup: {
      display: 'flex',
      justifyContent: 'center',
      gap: '0.5rem',
      marginTop: '0.8rem'
    },
    error: {
      color: 'red',
      textAlign: 'center'
    },
    bottomMessage: {
      color: '#007bff',
      textAlign: 'center',
      marginTop: '1rem'
    }
  };

  return (
    <div style={styles.page}>
      <h2 style={styles.header}>Your Matches</h2>
      <div style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
        <button style={styles.button} onClick={() => navigate('/update')}>
          Update Preferences
        </button>
      </div>
      {error && <p style={styles.error}>{typeof error === 'object' ? JSON.stringify(error) : error}</p>}
      <div style={styles.cardContainer}>
        {matches.length > 0 ? matches.map(match => (
          <TinderCard
            key={match.user.id}
            onSwipe={(dir) => onSwipe(dir, match.user.id)}
            onCardLeftScreen={() => onCardLeftScreen(match.user.id)}
            preventSwipe={[]}
          >
            <div style={styles.card}>
              <div style={styles.emoji}>{getEngineerEmoji(match.user)}</div>
              <div style={styles.cardInfo}>
                <h3 style={styles.username}>{getUsername(match.user)}</h3>
                <p style={styles.details}>
                  <strong>Common Days:</strong> {match.commonDays.join(', ')}
                </p>
                <p style={styles.details}>
                  <strong>Common Subjects:</strong> {match.commonSubjects.join(', ')}
                </p>
                <div style={styles.buttonGroup}>
                  <button
                    style={{ ...styles.button, backgroundColor: '#f87171' }}
                    onClick={() => onSwipe('left', match.user.id)}
                  >
                    ❌ Skip
                  </button>
                  <button
                    style={{ ...styles.button, backgroundColor: '#34d399' }}
                    onClick={() => onSwipe('right', match.user.id)}
                  >
                    ✅ Match
                  </button>
                </div>
              </div>
            </div>
          </TinderCard>
        )) : (
          <p style={{ textAlign: 'center', color: '#777' }}>
            {Array.isArray(matches) ? "No matches available." : matches}
          </p>
        )}
      </div>
      {bottomMessage && <p style={styles.bottomMessage}>{bottomMessage}</p>}
    </div>
  );
}

export default MatchList;
