import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './UpdatePreference.css'; // ⬅️ Don't forget this line

const DAYS = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const SUBJECTS = [
  "Calculus 1", "Calculus 2", "Vector Calculus", "Differential Equations", "Linear Algebra", "Probability", 
  "Physics 1", "Physics 2", "Physics 3", "General Chemistry", "Organic Chemistry", "Biochemistry", 
  "Electronics I", "Electronics II", "Digital Logic", "Computer Architecture", "Data Structures and Algorithms",
  "Operating Systems", "Computer Networks", "Software Engineering", 
  "Concrete Structures", "Steel Structures", "Transportation Engineering", "Environmental Engineering",
  "Thermodynamics", "Fluid Mechanics", "Heat Transfer", "Mechanical Design", "Manufacturing Processes"
];

function UpdatePreference({ userId }) {
  const navigate = useNavigate();
  const [selectedDays, setSelectedDays] = useState([]);
  const [subjectPreferences, setSubjectPreferences] = useState(() =>
    SUBJECTS.reduce((acc, subject) => ({ ...acc, [subject]: 50 }), {})
  );
  const [message, setMessage] = useState('');

  const handleDayClick = (day) => {
    setSelectedDays(prev =>
      prev.includes(day) ? prev.filter(d => d !== day) : [...prev, day]
    );
  };

  const handleSliderChange = (subject, value) => {
    setSubjectPreferences(prevPrefs => ({ ...prevPrefs, [subject]: Number(value) }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const subjectsToLearn = [];
    const subjectsToTeach = [];

    Object.entries(subjectPreferences).forEach(([subject, value]) => {
      if (value < 40) subjectsToLearn.push(subject);
      else if (value > 60) subjectsToTeach.push(subject);
    });

    const preferenceData = {
      availableDays: selectedDays.join(', '),
      subjectsToLearn: subjectsToLearn.join(', '),
      subjectsToTeach: subjectsToTeach.join(', ')
    };

    axios.post(`/api/user/${userId}/preference`, preferenceData)
      .then(() => {
        setMessage('Preference updated successfully.');
        navigate('/matchlist');
      })
      .catch(error => {
        setMessage(error.response?.data || 'Update failed.');
      });
  };

  return (
    <div className="preference-container">
      <h2>Update Preferences</h2>
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend><b>Available Days</b></legend>
          <div className="day-selector">
            {DAYS.map(day => (
              <span
                key={day}
                className={`day-pill ${selectedDays.includes(day) ? 'selected' : ''}`}
                onClick={() => handleDayClick(day)}
              >
                {day}
              </span>
            ))}
          </div>
        </fieldset>

        <fieldset>
          <legend><b>Subject Preferences</b></legend>
          <table className="subject-table">
            <thead>
              <tr>
                <th>Learn</th>
                <th>← Preference →</th>
                <th>Teach</th>
              </tr>
            </thead>
            <tbody>
              {SUBJECTS.map(subject => (
                <tr key={subject}>
                  <td>📚</td>
                  <td>
                    <input
                      type="range"
                      min="0"
                      max="100"
                      value={subjectPreferences[subject]}
                      onChange={(e) => handleSliderChange(subject, e.target.value)}
                    />
                    <div className="subject-label">{subject}</div>
                  </td>
                  <td>🧑‍🏫</td>
                </tr>
              ))}
            </tbody>
          </table>
        </fieldset>

        <div className="submit-btn-container">
          <button type="submit">Update Preference</button>
        </div>
      </form>
      {message && <p className="error-msg">{message}</p>}
    </div>
  );
}

export default UpdatePreference;
