import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function MatchList({ userId }) {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');
  const [bottomMessage, setBottomMessage] = useState('');
  const navigate = useNavigate();

  const cuteAnimalEmojiMap = {
    "electrical engineering": "🐭",
    "mechanical engineering": "🐱",
    "civil engineering": "🐶",
    "chemical engineering": "🐹",
    "general engineering": "🐻",
    "computer science": "🦊"
  };

  const getEngineerEmoji = (user) => {
    if (user.major) {
      const normalizedMajor = user.major.trim().toLowerCase();
      return cuteAnimalEmojiMap[normalizedMajor] || "🐱";
    }
    return "🐱";
  };

  const fetchMatches = () => {
    axios.get(`/api/matches/find/${userId}`)
      .then(response => {
        if (Array.isArray(response.data)) {
          setMatches(response.data);
          setError('');
        } else {
          setMatches([]);
          setError(response.data);
        }
      })
      .catch(err => {
        setError(err.response?.data || 'Error fetching matches');
        setMatches([]);
      });
  };

  useEffect(() => {
    if (userId) fetchMatches();
  }, [userId]);

  const getUsername = (user) => {
    return user.name && user.name.trim() !== "" ? user.name : user.email.split('@')[0];
  };

  const onSwipe = (direction, matchUserId) => {
    if (direction === 'right') {
      axios.post(`/api/matches/swipe?user1Id=${userId}&user2Id=${matchUserId}`)
        .then(response => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(response.data?.message || '');
        })
        .catch(err => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(err.response?.data || 'Error processing swipe.');
        });
    } else {
      setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
      setBottomMessage('');
    }
  };

  const styles = {
    page: {
      background: 'linear-gradient(135deg, #e0f7fa, #ffffff)',
      minHeight: '100vh',
      padding: '2rem 1rem',
      fontFamily: 'Arial, sans-serif',
      textAlign: 'center',
    },
    header: {
      color: '#2c6e6a',
      marginBottom: '1rem',
      fontSize: '2rem',
    },
    button: {
      backgroundColor: '#5ccdc1',
      color: '#fff',
      padding: '0.6rem 1.2rem',
      border: 'none',
      borderRadius: '20px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
      fontSize: '1rem',
      margin: '0.5rem'
    },
    cardContainer: {
      width: '90vw',
      maxWidth: '320px',
      height: '500px',
      margin: '2rem auto',
      position: 'relative',
    },
    card: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      backgroundColor: '#ffffff',
      borderRadius: '20px',
      boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      overflow: 'hidden',
    },
    emoji: {
      fontSize: '6rem',
      marginTop: '2rem'
    },
    cardDetails: {
      backgroundColor: '#2c6e6a',
      color: '#ffffff',
      padding: '1rem',
      width: '100%',
      position: 'absolute',
      bottom: 0,
      borderRadius: '0 0 20px 20px',
      textAlign: 'left'
    },
    username: {
      fontSize: '1.5rem',
      marginBottom: '0.5rem'
    },
    detailsText: {
      fontSize: '0.9rem',
      margin: '0.2rem 0'
    },
    bottomMessage: {
      color: 'blue',
      marginTop: '1rem'
    },
  };

  return (
    <div style={styles.page}>
      <h2 style={styles.header}>Match List</h2>
      <button style={styles.button} onClick={() => navigate('/update')}>
        Update Preferences
      </button>

      {error && <p style={styles.bottomMessage}>{typeof error === 'object' ? JSON.stringify(error) : error}</p>}

      <div style={styles.cardContainer}>
        {matches.map((match, index) => (
          <TinderCard
            key={match.user.id}
            onSwipe={(dir) => onSwipe(dir, match.user.id)}
            preventSwipe={[]}
          >
            <div style={{...styles.card, zIndex: matches.length - index}}>
              <div style={styles.emoji}>{getEngineerEmoji(match.user)}</div>
              <div style={styles.cardDetails}>
                <div style={styles.username}>{getUsername(match.user)}</div>
                <p style={styles.detailsText}><strong>Common Days:</strong> {match.commonDays.join(', ')}</p>
                <p style={styles.detailsText}><strong>Common Subjects:</strong> {match.commonSubjects.join(', ')}</p>
                <div style={{textAlign: 'center', marginTop: '1rem'}}>
                  <button style={{...styles.button, backgroundColor: '#f87171'}} onClick={() => onSwipe('left', match.user.id)}>Swipe Left</button>
                  <button style={{...styles.button, backgroundColor: '#34d399'}} onClick={() => onSwipe('right', match.user.id)}>Swipe Right</button>
                </div>
              </div>
            </div>
          </TinderCard>
        ))}
        {matches.length === 0 && <p style={{color: '#777'}}>No matches available.</p>}
      </div>

      {bottomMessage && <p style={styles.bottomMessage}>{bottomMessage}</p>}
    </div>
  );
}

export default MatchList;
