import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function MatchList({ userId }) {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');
  const [bottomMessage, setBottomMessage] = useState('');
  const navigate = useNavigate();

  const cuteAnimalEmojiMap = {
    "electrical engineering": "🐭",
    "mechanical engineering": "🐱",
    "civil engineering": "🐶",
    "chemical engineering": "🐹",
    "general engineering": "🐻",
    "computer science": "🦊"
  };

  const getEngineerEmoji = (user) => {
    if (user.major) {
      const normalizedMajor = user.major.trim().toLowerCase();
      return cuteAnimalEmojiMap[normalizedMajor] || "🐱";
    }
    return "🐱";
  };

  useEffect(() => {
    axios.get(`/api/matches/find/${userId}`)
      .then(response => {
        if (Array.isArray(response.data)) {
          setMatches(response.data);
          setError('');
        } else {
          setMatches([]);
          setError(response.data);
        }
      })
      .catch(err => {
        setError(err.response?.data || 'Error fetching matches');
        setMatches([]);
      });
  }, [userId]);

  const getUsername = (user) => {
    return user.name && user.name.trim() !== "" ? user.name : user.email.split('@')[0];
  };

  const onSwipe = (direction, matchUserId) => {
    if (direction === 'right') {
      axios.post(`/api/matches/swipe?user1Id=${userId}&user2Id=${matchUserId}`)
        .then(response => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(response.data.message || '');
        })
        .catch(err => {
          setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
          setBottomMessage(err.response?.data || 'Error processing swipe.');
        });
    } else {
      setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
      setBottomMessage('');
    }
  };

  const styles = {
    container: {
      maxWidth: '600px',
      margin: '2rem auto',
      padding: '2rem',
      backgroundColor: '#ffffff',
      borderRadius: '16px',
      boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
      fontFamily: 'Arial, sans-serif',
      color: '#2c6e6a',
      textAlign: 'center'
    },
    header: {
      fontSize: '1.8rem',
      marginBottom: '1.5rem',
      color: '#5ccdc1'
    },
    updateButton: {
      backgroundColor: '#5ccdc1',
      color: '#fff',
      padding: '10px 20px',
      fontSize: '1rem',
      border: 'none',
      borderRadius: '20px',
      cursor: 'pointer',
      marginBottom: '1.5rem'
    },
    cardWrapper: {
      position: 'relative',
      width: '100%',
      height: '500px'
    },
    card: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      backgroundColor: '#f5f5f5',
      borderRadius: '20px',
      boxShadow: '0 6px 12px rgba(0,0,0,0.1)',
      padding: '1.5rem',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between'
    },
    emoji: {
      fontSize: '5rem',
      marginBottom: '1rem'
    },
    username: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      color: '#2c6e6a',
      marginBottom: '0.5rem'
    },
    infoText: {
      fontSize: '0.95rem',
      color: '#555',
      margin: '0.25rem 0'
    },
    buttonGroup: {
      display: 'flex',
      justifyContent: 'space-evenly',
      marginTop: '1rem'
    },
    button: {
      borderRadius: '50%',
      width: '48px',
      height: '48px',
      fontSize: '1.4rem',
      border: 'none',
      color: '#fff',
      cursor: 'pointer'
    },
    rejectButton: {
      backgroundColor: '#ff6b6b'
    },
    acceptButton: {
      backgroundColor: '#5ccdc1'
    },
    message: {
      color: 'blue',
      marginTop: '1.5rem'
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Match List</h2>
      <button style={styles.updateButton} onClick={() => navigate('/update')}>Update Preference</button>

      {error && <p style={styles.message}>{error}</p>}

      <div style={styles.cardWrapper}>
        {matches.length > 0 ? matches.map((match) => (
          <TinderCard
            key={match.user.id}
            onSwipe={(dir) => onSwipe(dir, match.user.id)}
            preventSwipe={[]}
          >
            <div style={styles.card}>
              <div>
                <div style={styles.emoji}>{getEngineerEmoji(match.user)}</div>
                <div style={styles.username}>{getUsername(match.user)}</div>
                <p style={styles.infoText}><strong>Common Days:</strong> {match.commonDays.join(', ')}</p>
                <p style={styles.infoText}><strong>Subjects:</strong> {match.commonSubjects.join(', ')}</p>
              </div>
              <div style={styles.buttonGroup}>
                <button className="pressable" style={{ ...styles.button, ...styles.rejectButton }} onClick={() => onSwipe('left', match.user.id)}>✖️</button>
                <button className="pressable" style={{ ...styles.button, ...styles.acceptButton }} onClick={() => onSwipe('right', match.user.id)}>✔️</button>
              </div>
            </div>
          </TinderCard>
        )) : (
          <p>No matches available.</p>
        )}
      </div>

      {bottomMessage && <p style={styles.message}>{bottomMessage}</p>}
    </div>
  );
}

export default MatchList;
