import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function MatchList({ userId }) {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');
  const [bottomMessage, setBottomMessage] = useState('');
  const navigate = useNavigate();

  // Mapping of majors to cute animal emojis.
  const cuteAnimalEmojiMap = {
    "electrical engineering": "🐭",
    "mechanical engineering": "🐱",
    "civil engineering": "🐶",
    "chemical engineering": "🐹",
    "general engineering": "🐻",
    "computer science": "🦊"
  };

  // Returns a cute animal emoji based on the user's major.
  const getEngineerEmoji = (user) => {
    if (user.major) {
      const normalizedMajor = user.major.trim().toLowerCase();
      return cuteAnimalEmojiMap[normalizedMajor] || "🐱";
    }
    return "🐱";
  };

  // Fetch matches from backend.
  const fetchMatches = () => {
    axios.get(`/api/matches/find/${userId}`)
      .then(response => {
        setMatches(Array.isArray(response.data) ? response.data : []);
        setError(Array.isArray(response.data) ? '' : response.data);
      })
      .catch(err => {
        setError(err.response?.data || 'Error fetching matches');
        setMatches([]);
      });
  };

  useEffect(() => {
    if (userId) fetchMatches();
  }, [userId]);

  // Get username from user.
  const getUsername = (user) => {
    return user.name && user.name.trim() !== "" ? user.name : user.email.split('@')[0];
  };

  // onSwipe callback.
  const onSwipe = (direction, matchUserId) => {
    axios.post(`/api/matches/swipe?user1Id=${userId}&user2Id=${matchUserId}`)
      .then(response => {
        setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
        setBottomMessage(response.data?.message || '');
      })
      .catch(err => {
        setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
        setBottomMessage(err.response?.data || 'Error processing swipe.');
      });
  };

  const onCardLeftScreen = (matchUserId) => {
    console.log("Card for user ${matchUserId} left the scree");
  };

  // Modernized styles to match your theme.
  const styles = {
    page: {
      background: 'linear-gradient(135deg, #e0f7fa, #ffffff)',
      minHeight: '100vh',
      padding: '2rem 1rem',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      textAlign: 'center'
    },
    header: {
      fontSize: '2rem',
      marginBottom: '1rem',
      color: '#2c6e6a'
    },
    cardContainer: {
      position: 'relative',
      width: '340px',
      height: '520px',
      margin: '0 auto',
      marginTop: '1rem'
    },
    card: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      backgroundColor: '#ffffff',
      borderRadius: '20px',
      boxShadow: '0 8px 16px rgba(0,0,0,0.15)',
      display: 'flex',
      flexDirection: 'column',
      overflow: 'hidden'
    },
    imageContainer: {
      flex: '1 1 auto',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '1rem',
      backgroundColor: '#a7ffeb'
    },
    emoji: {
      fontSize: '6rem'
    },
    textContainer: {
      backgroundColor: 'rgba(0,0,0,0.6)',
      padding: '1rem',
      color: '#ffffff',
      textAlign: 'center'
    },
    username: {
      fontSize: '1.8rem',
      fontWeight: '700',
      letterSpacing: '1px',
      textShadow: '0px 1px 3px rgba(0,0,0,0.3)',
      marginBottom: '0.5rem'
    },
    details: {
      fontSize: '0.9rem',
      margin: '0.3rem 0'
    },
    buttonContainer: {
      display: 'flex',
      justifyContent: 'space-around',
      marginTop: '1rem'
    },
    actionButton: {
      width: '50px',
      height: '50px',
      fontSize: '1.5rem',
      border: 'none',
      borderRadius: '50%',  // Rounded button
      cursor: 'pointer',
      color: '#fff',
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
      transition: 'transform 0.2s ease'
    },
    updateBtn: {
      padding: '0.6rem 1.2rem',
      backgroundColor: '#5ccdc1',
      color: '#fff',
      border: 'none',
      borderRadius: '25px',
      cursor: 'pointer',
      fontSize: '1rem',
      boxShadow: '0 3px 6px rgba(0,0,0,0.1)',
      marginBottom: '1rem'
    },
    bottomMessage: {
      color: '#3498db',
      marginTop: '1rem'
    }
  };

  return (
    <div style={styles.page}>
      <h2 style={styles.header}>Your Matches</h2>
      <button style={styles.updateBtn} onClick={() => navigate('/update')}>
        Update Preferences
      </button>
      {error && <p style={styles.bottomMessage}>{typeof error === 'object' ? JSON.stringify(error) : error}</p>}
      <div style={styles.cardContainer}>
        {Array.isArray(matches) && matches.length > 0 ? (
          matches.map((match, index) => (
            <TinderCard
              key={match.user.id}
              onSwipe={(dir) => onSwipe(dir, match.user.id)}
              onCardLeftScreen={() => onCardLeftScreen(match.user.id)}
              preventSwipe={[]}
            >
              <div style={{ ...styles.card, zIndex: matches.length - index }}>
                <div style={styles.imageContainer}>
                  <span style={styles.emoji}>{getEngineerEmoji(match.user)}</span>
                </div>
                <div style={styles.textContainer}>
                  <h3 style={styles.username}>{getUsername(match.user)}</h3>
                  <p style={styles.details}><strong>Common Days:</strong> {match.commonDays.join(', ')}</p>
                  <p style={styles.details}><strong>Common Subjects:</strong> {match.commonSubjects.join(', ')}</p>
                  <div style={styles.buttonContainer}>
                    <button 
                      style={{ ...styles.actionButton, backgroundColor: '#ff7675' }}
                      onClick={() => onSwipe('left', match.user.id)}
                    >
                      ✖
                    </button>
                    <button 
                      style={{ ...styles.actionButton, backgroundColor: '#55efc4' }}
                      onClick={() => onSwipe('right', match.user.id)}
                    >
                      ✔
                    </button>
                  </div>
                </div>
              </div>
            </TinderCard>
          ))
        ) : (
          <p style={{ color: '#777', marginTop: '2rem' }}>
            {Array.isArray(matches) ? "No matches available." : matches}
          </p>
        )}
      </div>
      {bottomMessage && <p style={styles.bottomMessage}>{bottomMessage}</p>}
    </div>
  );
}

export default MatchList;
