// src/App.js
import React, { useState } from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import './App.css';

// Import your components (Dashboard removed)
import Landing from './components/Landing';
import Signup from './components/Signup';
import Login from './components/Login';
import UpdatePreference from './components/UpdatePreference';
import MatchList from './components/MatchList';
import Profile from './components/Profile';

function App() {
  const [userId, setUserId] = useState(null);

  return (
    <div>
      {/* Header with logo and website links */}
      <div className="header">
        <img src="/logo.png" alt="StudBud Logo" />
        <h1>StudBud</h1>
      </div>

      {/* Navigation bar visible only if logged in */}
      {userId && (
        <nav>
          <ul>
            <li><Link to="/matchlist">Matches</Link></li>
            <li><Link to="/update">Update Preferences</Link></li>
            <li><Link to="/profile">Profile</Link></li>
            <li>
              <button onClick={() => setUserId(null)}>Logout</button>
            </li>
          </ul>
        </nav>
      )}

      {/* Main container for the routes */}
      <div className="container">
        <Routes>
          {/* If user is signed in, default route is the match page */}
          <Route path="/" element={userId ? <Navigate to="/matchlist" /> : <Landing />} />
          <Route path="/signup" element={<Signup setUserId={setUserId} />} />
          <Route path="/login" element={<Login setUserId={setUserId} />} />
          <Route
            path="/matchlist"
            element={userId ? <MatchList userId={userId} /> : <Navigate to="/login" />}
          />
          <Route
            path="/update"
            element={userId ? <UpdatePreference userId={userId} /> : <Navigate to="/login" />}
          />
          <Route
            path="/profile"
            element={userId ? <Profile userId={userId} setUserId={setUserId} /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </div>
  );
}

export default App;
