import { useState } from 'react';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase';

function Login() {
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');

  const login=()=>signInWithEmailAndPassword(auth,email,password)
    .then(user=>localStorage.setItem('userId',user.user.uid))
    .catch(err=>alert(err.message));

  return <div><input placeholder="email" onChange={e=>setEmail(e.target.value)} /><input type="password" placeholder="pass" onChange={e=>setPassword(e.target.value)} /><button onClick={login}>Login</button></div>;
}

export default Login;
