import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { getAuth, createUserWithEmailAndPassword } from 'firebase/auth';

const allowedMajors = [
  "Electrical Engineering",
  "Mechanical Engineering",
  "Chemical Engineering",
  "Civil Engineering",
  "General Engineering",
  "Computer Science"
];

function Signup({ setUserId }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    major: '',
    year: ''
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const auth = getAuth();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const year = parseInt(formData.year, 10);
    if (isNaN(year) || year < 2020 || year > 2050) {
      setMessage("Year must be a number between 2020 and 2050.");
      return false;
    }
    if (!allowedMajors.includes(formData.major)) {
      setMessage("Please select a valid major.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    if (!validateForm()) return;

    try {
      // Create the user in Firebase Authentication
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );
      // Retrieve the Firebase ID token
      const token = await userCredential.user.getIdToken();

      // Send the signup request to your backend with the token in the header
      await axios.post(
        '/api/auth/signup',
        formData,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );

      // Redirect user to login after successful signup
      navigate('/login');
    } catch (error) {
      setMessage(error.response?.data || error.message || 'Signup failed');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="name" 
          placeholder="Name" 
          onChange={handleChange} 
          value={formData.name} 
          required 
        /><br/><br/>
        <input 
          type="email" 
          name="email" 
          placeholder="Email (@cooper.edu)" 
          onChange={handleChange} 
          value={formData.email} 
          required 
        /><br/><br/>
        <input 
          type="password" 
          name="password" 
          placeholder="Password (min 9 characters)" 
          onChange={handleChange} 
          value={formData.password} 
          required 
        /><br/><br/>
        <select name="major" value={formData.major} onChange={handleChange} required>
          <option value="">Select Major</option>
          {allowedMajors.map((major, index) => (
            <option key={index} value={major}>{major}</option>
          ))}
        </select><br/><br/>
        <input 
          type="number" 
          name="year" 
          placeholder="Year (2020-2050)" 
          onChange={handleChange} 
          value={formData.year} 
          min="2020" 
          max="2050" 
          required 
        /><br/><br/>
        <button type="submit">Sign Up</button>
      </form>
      {message && <p style={{ color: 'red' }}>{message}</p>}
      <p>
        Already have an account? <Link to="/login">Log in here</Link>
      </p>
    </div>
  );
}

export default Signup;
