import React, { useState, useEffect } from 'react';
import api from '../api';
import { getAuth } from "firebase/auth";
import { firebaseApp } from '../firebase';

function Profile() {
  const [profile, setProfile] = useState(null);
  const [message, setMessage] = useState('');
  const userId = localStorage.getItem('userId');

  // States for preference form fields
  const [major, setMajor] = useState('');
  const [year, setYear] = useState('');
  const [availableDays, setAvailableDays] = useState('');
  const [subjectsToLearn, setSubjectsToLearn] = useState('');
  const [subjectsToTeach, setSubjectsToTeach] = useState('');

  useEffect(() => {
    if (userId) {
      fetchProfile();
    } else {
      setMessage("Please login to view your profile.");
    }
  }, [userId]);

  // When profile is fetched, initialize the preference form fields
  useEffect(() => {
    if (profile && profile.preference) {
      setMajor(profile.preference.major || '');
      setYear(profile.preference.year || '');
      setAvailableDays(profile.preference.availableDays || '');
      setSubjectsToLearn(profile.preference.subjectsToLearn || '');
      setSubjectsToTeach(profile.preference.subjectsToTeach || '');
    }
  }, [profile]);

  const fetchProfile = async () => {
    try {
      const response = await api.get(`/api/user/${userId}`);
      setProfile(response.data);
    } catch (error) {
      console.error(error);
      setMessage("Error fetching profile.");
    }
  };

  const updatePreference = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post(`/api/user/${userId}/preference`, {
        major,
        year,
        availableDays,
        subjectsToLearn,
        subjectsToTeach
      });
      alert("Preference updated successfully.");
      // Update profile state with new preference data
      setProfile({ ...profile, preference: response.data });
    } catch (error) {
      console.error(error);
      alert("Error updating preferences.");
    }
  };

  const handleDeleteAccount = async () => {
    const confirmDelete = window.confirm("Are you sure you want to delete your account? This action cannot be undone.");
    if (!confirmDelete) return;
    try {
      const auth = getAuth(firebaseApp);
      const user = auth.currentUser;
      if (!user) {
        alert("No authenticated user found.");
        return;
      }
      const token = await user.getIdToken();
      await api.delete('/api/auth/delete', {
        data: {
          email: profile.email,
          firebaseToken: token
        }
      });
      alert("Account deleted successfully.");
      localStorage.removeItem('userId');
      setProfile(null);
      // Optionally, redirect to the login page here.
    } catch (error) {
      console.error(error);
      alert("Error deleting account.");
    }
  };

  if (!profile) return <div>{message || "Loading..."}</div>;

  return (
    <div>
      <h2>Profile</h2>
      <p><strong>Name:</strong> {profile.name}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Firebase UID:</strong> {profile.firebaseUid}</p>
      {profile.preference && (
        <div>
          <h3>Current Preferences</h3>
          <p><strong>Major:</strong> {profile.preference.major}</p>
          <p><strong>Year:</strong> {profile.preference.year}</p>
          <p><strong>Available Days:</strong> {profile.preference.availableDays}</p>
          <p><strong>Subjects to Learn:</strong> {profile.preference.subjectsToLearn}</p>
          <p><strong>Subjects to Teach:</strong> {profile.preference.subjectsToTeach}</p>
        </div>
      )}
      <hr />
      <h3>Update Preferences</h3>
      <form onSubmit={updatePreference}>
        <div>
          <label>Major:</label>
          <input type="text" value={major} onChange={e => setMajor(e.target.value)} />
        </div>
        <div>
          <label>Year:</label>
          <input type="text" value={year} onChange={e => setYear(e.target.value)} />
        </div>
        <div>
          <label>Available Days (comma-separated):</label>
          <input type="text" value={availableDays} onChange={e => setAvailableDays(e.target.value)} />
        </div>
        <div>
          <label>Subjects to Learn (comma-separated):</label>
          <input type="text" value={subjectsToLearn} onChange={e => setSubjectsToLearn(e.target.value)} />
        </div>
        <div>
          <label>Subjects to Teach (comma-separated):</label>
          <input type="text" value={subjectsToTeach} onChange={e => setSubjectsToTeach(e.target.value)} />
        </div>
        <button type="submit">Update Preferences</button>
      </form>
      <hr />
      <h3>Delete Account</h3>
      <button 
        onClick={handleDeleteAccount} 
        style={{ backgroundColor: 'red', color: 'white', padding: '10px', border: 'none' }}
      >
        Delete Account
      </button>
    </div>
  );
}

export default Profile;
