import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DAYS = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const SUBJECTS = [
  "Calculus 1", "Differential Equations", "Linear Algebra", "Probability", 
  "Physics", "General Chemistry", "Organic Chemistry", "Biochemistry", 
  "Electronics I", "Electronics II", "Digital Logic", "Computer Architecture", "Data Structures and Algorithms",
  "Operating Systems", "Computer Networks", "Software Engineering", 
  "Concrete Structures", "Steel Structures", "Transportation Engineering", "Environmental Engineering",
  "Thermodynamics", "Fluid Mechanics", "Heat Transfer", "Mechanical Design", "Manufacturing Processes"
];

function UpdatePreference({ userId }) {
  const navigate = useNavigate();
  const [selectedDays, setSelectedDays] = useState([]);
  const [subjectPreferences, setSubjectPreferences] = useState({});
  const [message, setMessage] = useState('');

  const handleCheckboxChange = (day) => {
    setSelectedDays(prevDays =>
      prevDays.includes(day)
        ? prevDays.filter(d => d !== day)
        : [...prevDays, day]
    );
  };

  const handleSliderChange = (subject, value) => {
    setSubjectPreferences(prevPrefs => ({ ...prevPrefs, [subject]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const subjectsToLearn = [];
    const subjectsToTeach = [];

    Object.entries(subjectPreferences).forEach(([subject, value]) => {
      if (value === 'learn') subjectsToLearn.push(subject);
      if (value === 'teach') subjectsToTeach.push(subject);
    });

    const preferenceData = {
      availableDays: selectedDays.join(', '),
      subjectsToLearn: subjectsToLearn.join(', '),
      subjectsToTeach: subjectsToTeach.join(', ')
    };

    axios.post(`/api/user/${userId}/preference`, preferenceData)
      .then(() => {
        setMessage('Preference updated successfully.');
        navigate('/matchlist');
      })
      .catch(error => {
        setMessage(error.response?.data || 'Update failed.');
      });
  };

  return (
    <div style={{ maxWidth: '700px', margin: '2rem auto', padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>Update Preferences</h2>
      <form onSubmit={handleSubmit}>

        <fieldset style={{ marginBottom: '1rem' }}>
          <legend><b>Available Days</b></legend>
          {DAYS.map(day => (
            <label key={day} style={{ marginRight: '1rem' }}>
              <input
                type="checkbox"
                checked={selectedDays.includes(day)}
                onChange={() => handleCheckboxChange(day)}
              />
              {day}
            </label>
          ))}
        </fieldset>

        <fieldset>
          <legend><b>Subject Preferences</b></legend>
          <table style={{ width: '100%', textAlign: 'center', marginTop: '1rem' }}>
            <thead>
              <tr>
                <th>Learn</th>
                <th>None</th>
                <th>Teach</th>
                <th style={{ textAlign: 'left', paddingLeft: '1rem' }}>Subject</th>
              </tr>
            </thead>
            <tbody>
              {SUBJECTS.map(subject => (
                <tr key={subject}>
                  <td>
                    <input
                      type="radio"
                      name={subject}
                      value="learn"
                      checked={subjectPreferences[subject] === 'learn'}
                      onChange={() => handleSliderChange(subject, 'learn')}
                    />
                  </td>
                  <td>
                    <input
                      type="radio"
                      name={subject}
                      value="none"
                      checked={!subjectPreferences[subject] || subjectPreferences[subject] === 'none'}
                      onChange={() => handleSliderChange(subject, 'none')}
                    />
                  </td>
                  <td>
                    <input
                      type="radio"
                      name={subject}
                      value="teach"
                      checked={subjectPreferences[subject] === 'teach'}
                      onChange={() => handleSliderChange(subject, 'teach')}
                    />
                  </td>
                  <td style={{ textAlign: 'left', paddingLeft: '1rem' }}>{subject}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </fieldset>

        <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
          <button type="submit">Update Preference</button>
        </div>
      </form>
      {message && <p style={{ color: 'red', textAlign: 'center', marginTop: '1rem' }}>{message}</p>}
    </div>
  );
}

export default UpdatePreference;
