import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function MatchList({ userId }) {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');
  const [bottomMessage, setBottomMessage] = useState('');
  const navigate = useNavigate();

  const cuteAnimalEmojiMap = {
    "electrical engineering": "🐭",
    "mechanical engineering": "🐱",
    "civil engineering": "🐶",
    "chemical engineering": "🐹",
    "general engineering": "🐻",
    "computer science": "🦊"
  };

  const getEngineerEmoji = (user) => {
    if (user.major) {
      const normalizedMajor = user.major.trim().toLowerCase();
      return cuteAnimalEmojiMap[normalizedMajor] || "🐱";
    }
    return "🐱";
  };

  const fetchMatches = () => {
    axios.get(`/api/matches/find/${userId}`)
      .then(response => {
        setMatches(Array.isArray(response.data) ? response.data : []);
        setError(Array.isArray(response.data) ? '' : response.data);
      })
      .catch(err => {
        setError(err.response?.data || 'Error fetching matches');
        setMatches([]);
      });
  };

  useEffect(() => {
    if (userId) fetchMatches();
  }, [userId]);

  const getUsername = (user) => {
    return user.name && user.name.trim() !== "" ? user.name : user.email.split('@')[0];
  };

  const onSwipe = (direction, matchUserId) => {
    axios.post(`/api/matches/swipe?user1Id=${userId}&user2Id=${matchUserId}`)
      .then(response => {
        setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
        setBottomMessage(response.data?.message || '');
      })
      .catch(err => {
        setMatches(prev => prev.filter(match => match.user.id !== matchUserId));
        setBottomMessage(err.response?.data || 'Error processing swipe.');
      });
  };

  const pageStyle = {
    background: 'linear-gradient(135deg, #a7ffeb, #ffffff)',
    minHeight: '100vh',
    padding: '2rem 0',
    textAlign: 'center',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
  };

  const headerStyle = {
    color: '#34495e',
    fontSize: '2rem',
    marginBottom: '1.5rem'
  };

  const cardContainerStyle = {
    position: 'relative',
    width: '340px',
    height: '520px',
    margin: '0 auto'
  };

  const cardStyle = {
    position: 'absolute',
    backgroundColor: '#ffffff',
    width: '340px',
    height: '520px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.15)',
    borderRadius: '20px',
    display: 'flex',
    flexDirection: 'column',
    overflow: 'hidden'
  };

  const imageContainerStyle = {
    flex: '1',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '1rem',
    background: 'linear-gradient(45deg, #74ebd5, #ACB6E5)'
  };

  const textContainerStyle = {
    padding: '1rem',
    backgroundColor: '#34495e',
    color: '#ecf0f1'
  };

  const usernameStyle = {
    fontSize: '2rem',
    fontWeight: '700',
    color: '#fff',
    marginBottom: '0.5rem'
  };

  const emojiStyle = {
    fontSize: '7rem'
  };

  const actionButtonStyle = {
    width: '60px',
    height: '60px',
    fontSize: '1.5rem',
    border: 'none',
    borderRadius: '50%',
    cursor: 'pointer',
    boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
    transition: 'transform 0.2s ease'
  };

  return (
    <div style={pageStyle}>
      <h2 style={headerStyle}>✨ Your Matches ✨</h2>

      <button
        style={{ ...actionButtonStyle, backgroundColor: '#5ccdc1', width: 'auto', padding: '0.5rem 1rem', borderRadius: '30px', marginBottom: '1rem' }}
        onClick={() => navigate('/update')}
      >
        ⚙️ Update Preferences
      </button>

      {error && <p style={{ color: '#e74c3c' }}>{error}</p>}

      <div style={cardContainerStyle}>
        {matches.length ? matches.map((match) => (
          <TinderCard
            key={match.user.id}
            onSwipe={(dir) => onSwipe(dir, match.user.id)}
            preventSwipe={[]}
          >
            <div style={cardStyle}>
              <div style={imageContainerStyle}>
                <span style={emojiStyle}>{getEngineerEmoji(match.user)}</span>
              </div>
              <div style={textContainerStyle}>
                <h3 style={usernameStyle}>{getUsername(match.user)}</h3>
                <p><strong>📅 Common Days:</strong> {match.commonDays.join(', ')}</p>
                <p><strong>📚 Common Subjects:</strong> {match.commonSubjects.join(', ')}</p>
                <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '1rem' }}>
                  <button
                    style={{ ...actionButtonStyle, backgroundColor: '#ff7675' }}
                    onClick={() => onSwipe('left', match.user.id)}
                  >
                    ✖️
                  </button>
                  <button
                    style={{ ...actionButtonStyle, backgroundColor: '#55efc4' }}
                    onClick={() => onSwipe('right', match.user.id)}
                  >
                    ✔️
                  </button>
                </div>
              </div>
            </div>
          </TinderCard>
        )) : (
          <p style={{ color: '#34495e' }}>No matches available.</p>
        )}
      </div>

      {bottomMessage && <p style={{ color: '#0984e3', marginTop: '1rem' }}>{bottomMessage}</p>}
    </div>
  );
}

export default MatchList;
