// src/Signup.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';

const allowedMajors = [
  "Electrical Engineering",
  "Mechanical Engineering",
  "Chemical Engineering",
  "Civil Engineering",
  "General Engineering",
  "Computer Science"
];

function Signup({ setUserId }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    major: '',
    year: ''
  });
  const [message, setMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();
  const auth = getAuth();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const year = parseInt(formData.year, 10);
    if (isNaN(year) || year < 2020 || year > 2050) {
      setMessage("Year must be a number between 2020 and 2050.");
      return false;
    }
    if (!allowedMajors.includes(formData.major)) {
      setMessage("Please select a valid major.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isSubmitting) return; // Prevent duplicate submissions
    setMessage('');
    if (!validateForm()) return;
    setIsSubmitting(true);

    let token;
    try {
      // Create the user in Firebase (this should succeed for a new user)
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );
      token = await userCredential.user.getIdToken();

      // Call the backend signup endpoint
      await axios.post('/api/auth/signup', formData, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
    } catch (error) {
      // If the error indicates the email is already in use,
      // assume this account was created in a previous session.
      if (error.code === 'auth/email-already-in-use') {
        setMessage("User already exists. Please sign in.");
      } else {
        setMessage(error.response?.data || error.message || 'Signup failed');
      }
      setIsSubmitting(false);
      return;
    }

    // If signup was successful, automatically log the user in.
    try {
      const loginResponse = await axios.post(
        '/api/auth/login',
        { firebaseToken: token },
        { headers: { 'Authorization': `Bearer ${token}` } }
      );

      if (loginResponse.data && loginResponse.data.userId) {
        setUserId(loginResponse.data.userId);
        navigate('/dashboard');
      } else {
        setMessage("Login after signup failed: userId not returned.");
      }
    } catch (loginError) {
      setMessage(loginError.response?.data || loginError.message || 'Login failed');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="name" 
          placeholder="Name" 
          onChange={handleChange} 
          value={formData.name} 
          required
        /><br/><br/>
        <input 
          type="email" 
          name="email" 
          placeholder="Email (@cooper.edu)" 
          onChange={handleChange} 
          value={formData.email} 
          required
        /><br/><br/>
        <input 
          type="password" 
          name="password" 
          placeholder="Password (min 9 characters)" 
          onChange={handleChange} 
          value={formData.password} 
          required
        /><br/><br/>
        <select name="major" value={formData.major} onChange={handleChange} required>
          <option value="">Select Major</option>
          {allowedMajors.map((major) => (
            <option key={major} value={major}>{major}</option>
          ))}
        </select><br/><br/>
        <input 
          type="number" 
          name="year" 
          placeholder="Year (2020-2050)" 
          onChange={handleChange} 
          value={formData.year} 
          min="2020" 
          max="2050" 
          required
        /><br/><br/>
        <button type="submit" disabled={isSubmitting}>Sign Up</button>
      </form>
      {message && (
        <div style={{ marginTop: '1rem', textAlign: 'center' }}>
          <p style={{ color: 'red' }}>{message}</p>
          {message === "User already exists. Please sign in." && (
            <p>
              <Link to="/login">Click here to sign in</Link>
            </p>
          )}
        </div>
      )}
    </div>
  );
}

export default Signup;
