import React, { useState } from 'react';
import { getAuth, createUserWithEmailAndPassword, updateProfile } from 'firebase/auth';
import { firebaseApp } from '../firebase';
import api from '../api';

function Signup() {
  const auth = getAuth(firebaseApp);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [major, setMajor] = useState('');
  const [year, setYear] = useState('');
  const [message, setMessage] = useState('');

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      // Create user in Firebase
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      await updateProfile(user, { displayName: name });
      const token = await user.getIdToken();

      // Call backend signup endpoint. For secured endpoints the interceptor will attach token if available.
      const response = await api.post('/api/auth/signup', {
        name, email, password, major, year
      });
      setMessage(response.data);
    } catch (error) {
      console.error(error);
      setMessage(error.message);
    }
  };

  return (
    <div>
      <h2>Signup</h2>
      <form onSubmit={handleSignup}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={e => setName(e.target.value)} required />
        </div>
        <div>
          <label>Email (must be @cooper.edu):</label>
          <input type="email" value={email} onChange={e => setEmail(e.target.value)} required />
        </div>
        <div>
          <label>Password (min 9 characters):</label>
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
        </div>
        <div>
          <label>Major:</label>
          <input type="text" value={major} onChange={e => setMajor(e.target.value)} />
        </div>
        <div>
          <label>Year:</label>
          <input type="text" value={year} onChange={e => setYear(e.target.value)} />
        </div>
        <button type="submit">Signup</button>
      </form>
      { message && <p>{message}</p> }
    </div>
  );
}

export default Signup;
