import { useState } from 'react';
import { createUserWithEmailAndPassword, updateProfile } from 'firebase/auth';
import { auth } from '../firebase';
import api from '../api';

function Signup() {
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [name,setName]=useState('');
  const [major,setMajor]=useState('');
  const [year,setYear]=useState('');

  const signup=()=>createUserWithEmailAndPassword(auth,email,password)
    .then(async user=>{
      await updateProfile(user.user,{displayName:name});
      await api.post('/api/auth/signup',{name,email,password,major,year});
      localStorage.setItem('userId',user.user.uid);
    })
    .catch(err=>alert(err.message));

  return <div>
    <input placeholder="name" onChange={e=>setName(e.target.value)} />
    <input placeholder="email" onChange={e=>setEmail(e.target.value)} />
    <input type="password" placeholder="pass" onChange={e=>setPassword(e.target.value)} />
    <input placeholder="major" onChange={e=>setMajor(e.target.value)} />
    <input placeholder="year" onChange={e=>setYear(e.target.value)} />
    <button onClick={signup}>Signup</button>
  </div>;
}

export default Signup;
